#!/bin/bash
theme_name=barebones
echo -e "\e[32mDownloading the latest version of WordPress..." && curl https://wordpress.org/latest.zip -LOs && echo -e "\e[32mWordPress downloaded! Extracting files..." && unzip -q ./latest.zip && mv ./wordpress ./public && rm -rf ./latest.zip && rm -rf ./public/wp-content/plugins/akismet ./public/wp-content/plugins/hello.php ./public/wp-content/themes/twenty* && rm -rf ./public/license.txt && rm -rf ./public/license.txt && rm -rf ./public/readme.html && mv ./theme ./public/wp-content/themes/$theme_name && echo -e "\e[32mRemoved unused files and folders" && echo -e "\e[32mSuccessfully installed WordPress and Barebones theme!" && rm -- "$0"