#!/bin/bash
theme_name=barebones
echo -e "Downloading the latest version of WordPress..." && curl https://wordpress.org/latest.zip -LOs && echo -e "WordPress downloaded! Extracting files..." && unzip -q ./latest.zip && mv ./wordpress ./public && rm -rf ./latest.zip ./public/wp-content/plugins/akismet ./public/wp-content/plugins/hello.php ./public/wp-content/themes/twenty* ./public/license.txt ./public/license.txt ./public/readme.html && mv ./theme ./public/wp-content/themes/$theme_name && echo -e "Removed unused files and folders" && echo -e "Successfully installed WordPress and Barebones theme!" && rm -- "$0"